    def add_to_buffer(self):
        db = dbd.init_firebase()
        fac_mas = dbd.list_of_facilities()
        item = self.NameBox.currentIndex()
        name = fac_mas[item][0]
        cost = fac_mas[item][1]
        db.child("facilities").child("buffer").set({name:cost})


